#import "YPFileItem.h"
#import "YPFileManager.h"
#import "YPCategoryHeader.h"

@implementation YPFileItem

- (NSString *)stringForFileSize {
    if (self.isDirectory) {
        // 计算目录下文件数量
        NSFileManager *fileManager = [NSFileManager defaultManager];
        NSError *error = nil;
        NSArray *contents = [fileManager contentsOfDirectoryAtPath:self.path error:&error];
        if (error) {
            return @"0 items"; // 避免错误时崩溃
        }
        return [NSString stringWithFormat:@"%lu items", (unsigned long)contents.count];
    }
    double fileSize = self.size;
    NSArray *units = @[@"B", @"KB", @"MB", @"GB", @"TB"];
    int index = 0;
    while (fileSize >= 1024 && index < units.count - 1) {
        fileSize /= 1024;
        index++;
    }
    return [NSString stringWithFormat:@"%.2f %@", fileSize, units[index]];
}

- (UIImage *)thumbnail {
    if (!_thumbnail) {
        if (self.isDirectory) {
            return [UIImage yp_imageWithBase64String:[self fileBase64]];
        } else {
            return [UIImage yp_imageWithBase64String:[self unknowBase64]];
        }
    }
    return _thumbnail;
}

- (void)loadThumbnailWithCompletion:(void (^)(UIImage *thumbnail))completion {
    if (self.isLoadThumbnail) {
        dispatch_async(dispatch_get_main_queue(), ^{
            if (completion) {
                completion(self.thumbnail);
            }
        });
        return;
    }
    if (self.isDirectory) {
        self.thumbnail = [UIImage yp_imageWithBase64String:[self fileBase64]];
        self.isLoadThumbnail = YES;
        dispatch_async(dispatch_get_main_queue(), ^{
            if (completion) {
                completion(self.thumbnail);
            }
        });
        return;
    }
    dispatch_async(dispatch_get_global_queue(DISPATCH_QUEUE_PRIORITY_DEFAULT, 0), ^{
        UIImage *thumbnail = [[YPFileManager shareInstance] generateThumbnailForFileAtPath:self.path];
        if (thumbnail == nil) {
            if ([self isCompressedFile]) {
                self.thumbnail = [UIImage yp_imageWithBase64String:[self zipBase64]];
            } else {
                self.thumbnail = [UIImage yp_imageWithBase64String:[self unknowBase64]];
            }
        } else {
            self.thumbnail = thumbnail;
        }
        self.isLoadThumbnail = YES;
        dispatch_async(dispatch_get_main_queue(), ^{
            if (completion) {
                completion(self.thumbnail);
            }
        });
    });
}

/// 是否是压缩文件
- (BOOL)isCompressedFile {
    if ([self isCompressedByExtension]) {
        return YES;
    }
    return [self isCompressedByMagicNumber];
}

- (BOOL)isCompressedByExtension {
    NSString *extension = [[self.path pathExtension] lowercaseString];
    NSSet *compressedExtensions = [NSSet setWithObjects:@"zip", @"rar", @"7z", @"tar", @"gz", @"bz2", @"xz", @"tgz", @"tbz2", nil];
    return [compressedExtensions containsObject:extension];
}

- (BOOL)isCompressedByMagicNumber {
    NSFileHandle *fileHandle = [NSFileHandle fileHandleForReadingAtPath:self.path];
    if (!fileHandle) return NO;

    NSData *headerData = [fileHandle readDataOfLength:4];
    [fileHandle closeFile];

    if (headerData.length < 4) return NO;

    uint8_t bytes[4];
    [headerData getBytes:bytes length:4];

    return (bytes[0] == 0x50 && bytes[1] == 0x4B) ||  // ZIP
           (bytes[0] == 0x52 && bytes[1] == 0x61) ||  // RAR
           (bytes[0] == 0x37 && bytes[1] == 0x7A) ||  // 7z
           (bytes[0] == 0x1F && bytes[1] == 0x8B) ||  // GZ
           (bytes[0] == 0x42 && bytes[1] == 0x5A);    // BZ2
}

